import React, { useState, useEffect, useRef } from 'react';

//material ui package
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import useStyles from './Styles';

//packages
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
// import FileUpload from 'react-material-file-upload';

//components
import { createPost, updatePost } from '../../actions/Posts';

const Form = ({
  currentId, setCurrentId
}) => {
  const [title, setTitle] = useState('');
  const [message, setMessage] = useState('');
  const [tags, setTags] = useState('');
  const [file, setFile] = useState(null);
  const post = useSelector((state) => (currentId ? state.posts.posts.find((message) => message._id === currentId) : null));
  const dispatch = useDispatch();
  const classes = useStyles();
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('profile'));
  const inputRef = useRef();

  // onmonut
  useEffect(() => {
    // if (post) setPostData(post);
  }, [post]);

  /**
   * Reset input box
   */
  const clear = () => {
    setCurrentId(0);
    setMessage('');
    setTags('');
    setTitle('');
    inputRef.current.value = "";
  };


  /**
   * create user post 
   * @param {object} e 
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    const userMEessage = { title, tags, message };
    console.log(userMEessage)
    let formData = new FormData();
    formData.append('file', file);
    formData.append('title', title);
    formData.append('tags', tags);
    formData.append('message', message);
    if (currentId === 0) {
      dispatch(createPost(formData));
      clear();
    } else {
      dispatch(updatePost(currentId, formData, { name: user?.result?.name }));
      clear();
    }

  };

  if (!user?.result?.name) {
    return (
      <Paper className={classes.paper} elevation={6}>
        <Typography variant='h6' align='center'>
          Please Sign In to create your own memories and like other's memories...
        </Typography>
      </Paper>
    )
  }
  return (
    <Paper className={classes.paper} elevation={6}>
      <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}  >
        <Typography variant="h6">{currentId ? `Editing "${post.title}"` : 'Creating a Memory'}</Typography>

        <TextField
          name="title"
          variant="outlined"
          label="Title"
          fullWidth
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <TextField
          spellCheck={true}
          name="message"
          variant="outlined"
          label="Message"
          fullWidth
          multiline
          minRows={4}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <TextField
          name="tags"
          variant="outlined"
          label="Tags (coma separated)"
          fullWidth
          value={tags}
          onChange={(e) => setTags(e.target.value.split(','))}
        />
        <div className={classes.fileInput}>
          {/* <input
            name="file"
            type="file"
            ref={inputRef}
            onChange={(e) => setFile(e.target.files[0])}
          /> */}
          <input id="file-upload" type="file" name="fileUpload" accept=".jpeg, .doc, .docx, .xls, .xlsx, .txt, .jpg, .png, .gif" />

          <label for="file-upload" id="file-drag">
            <img id="file-image" src="#" alt="Preview" class="hidden"/>
              <div id="start">
                <i class="fa fa-download" aria-hidden="true"></i>
                <div>Select a file or drag here</div>
                <div id="notimage" class="hidden">Please select an image</div>
                <span id="file-upload-btn" class="btn btn-primary">Select a file</span>
              </div>
              <div id="response" class="hidden">
                <div id="messages"></div>
                <progress class="progress" id="file-progress" value="0">
                  <span>0</span>%
                </progress>
              </div>
          </label>
        </div>
        <Button
          className={classes.buttonSubmit}
          variant="contained"
          color="primary"
          size="large"
          type="submit"
          fullWidth
        >Submit
        </Button>
        <Button
          variant="contained"
          color="secondary"
          size="large"
          onClick={clear}
          fullWidth
        >Reset
        </Button>
      </form>
    </Paper>
  );
};

export default Form;
