import React, { useEffect, useState } from 'react';

//packages
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import moment from 'moment';

// packages ui
import {
    CircularProgress,
    Divider,
    Typography,
    Avatar,
    Button,
    Card,
    CardHeader,
    CardContent,
    CardMedia,
    IconButton,
    ButtonBase
} from '@material-ui/core';
import MoreVertIcon from '@material-ui/core/MoreVertIcon';
import Skeleton from '@material-ui/lab/Skeleton';


//component
import { follow, unFollow, userProfile } from '../../actions/Posts';

const Profile = () => {
    const { id } = useParams();
    const { user, userPosts, isLoading } = useSelector((state) => state.posts);
    const users = JSON.parse(localStorage.getItem('profile'));
    const dispatch = useDispatch()
    const url = "http://localhost:5000/";
    const navigate = useNavigate();
    const followId = users?.result?.googleId || users?.result?._id;
    const [showfollow, setShowFollow] = useState(user ? !user?.following?.includes(followId) : true)
    console.log(followId)
    //dispatch user profile req
    useEffect(() => {
        dispatch(userProfile(id));
    }, [id]);

    //post deatil page 
    const openPost = (id) => navigate(`/posts/${id}`);

    /**
     * follow user by user id
     * @param {object} user 
     */
    const handleFollowUser = (user) => {
        dispatch(follow(followId, user));
        setShowFollow(false)
    };

    /**
     * unfollow user 
     * @param {object} user 
     */
    const hnadleUnFollowUSer = (user) => {
        const unFollowId = followId;
        dispatch(unFollow(unFollowId, user));
        setShowFollow(true)
    };

    return (
        isLoading ? <CircularProgress /> : (
            <div style={{ maxWidth: "550px", margin: "0px auto" }}>
                <div style={{
                    display: "flex",
                    justifyContent: "space-around",
                    margin: "18px 0px",
                    borderBottom: "1px solid grey"
                }}>
                    <div>
                        <Avatar
                            style={{ width: "160px", height: "160px", borderRadius: "80px" }}
                            alt={user?.name}
                            src={user?.imageUrl}
                        >
                            {user?.name.charAt(0)}
                        </Avatar>
                    </div>
                    <div>
                        <Typography variant="h6"> {user?.name}</Typography>
                        <Typography variant="h6">{user?.email}</Typography>
                        <div style={{ display: "flex", justifyContent: "space-between", width: "108%" }}>
                            <h6>{userPosts?.length} posts</h6>
                            <h6>{user?.followers?.length} followers</h6>
                            <h6>{user?.following?.length} following</h6>
                        </div>
                        {users?.result?._id === user?._id ? (null) : (
                            <>
                                {showfollow ? (
                                    <Button
                                        style={{
                                            margin: "10px"
                                        }}
                                        variant='outlined'
                                        color='primary'
                                        onClick={() => handleFollowUser(user)}
                                    >Follow
                                    </Button>
                                ) : (
                                    <Button
                                        style={{
                                            margin: "10px"
                                        }}
                                        variant='contained'
                                        color='primary'
                                        onClick={() => hnadleUnFollowUSer(user)}
                                    >UnFollow
                                    </Button>
                                )}
                            </>)
                        }
                    </div>
                </div>
                <div className="gallery">
                    <Typography gutterBottom variant='h5'> Your Posts : </Typography>
                    <Divider />
                    {/* user posts */}
                    {userPosts?.map(item => {
                        return (
                            <Card sx={{ maxWidth: 345, m: 2 }}>
                                <ButtonBase
                                    onClick={() => openPost(item._id)}
                                >
                                    <CardHeader
                                        avatar={
                                            isLoading ? (
                                                <Skeleton animation="wave" variant="circular" width={40} height={40} />
                                            ) : (
                                                <Avatar
                                                    style={{ width: "160px", height: "160px", borderRadius: "80px" }}
                                                    alt={user?.name}
                                                    src={user?.imageUrl}
                                                >
                                                    {user?.name.charAt(0)}
                                                </Avatar>
                                            )
                                        }
                                        action={
                                            isLoading ? null : (
                                                <IconButton aria-label="settings">
                                                    <MoreVertIcon />
                                                </IconButton>
                                            )
                                        }
                                        title={
                                            isLoading ? (
                                                <Skeleton
                                                    animation="wave"
                                                    height={10}
                                                    width="80%"
                                                    style={{ marginBottom: 6 }}
                                                />
                                            ) : (
                                                <>{user?.name}</>
                                            )
                                        }
                                        subheader={
                                            isLoading ? (
                                                <Skeleton animation="wave" height={10} width="40%" />
                                            ) : (
                                                <>{moment(item.createdAt).fromNow()}</>
                                            )
                                        }
                                    />
                                    {isLoading ? (
                                        <Skeleton sx={{ height: 190 }} animation="wave" variant="rectangular" />
                                    ) : (
                                        <CardMedia
                                            component="img"
                                            height="140"
                                            image={url + item.file}
                                            alt="Nicola Sturgeon on a TED talk stage"
                                        />
                                    )}

                                    <CardContent>
                                        {isLoading ? (
                                            <React.Fragment>
                                                <Skeleton animation="wave" height={10} style={{ marginBottom: 6 }} />
                                                <Skeleton animation="wave" height={10} width="80%" />
                                            </React.Fragment>
                                        ) : (
                                            <Typography variant="body2" color="text.secondary" component="p">
                                                {
                                                    <>{item.title}</>
                                                }
                                            </Typography>
                                        )}
                                    </CardContent>
                                </ButtonBase>
                            </Card>
                        )
                    })}
                </div>
            </div >
        )
    );
}

export default Profile;
