import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { userProfile } from '../../actions/Posts';

const Profile = () => {
    const { id } = useParams();
    const { post, user, isLoading } = useSelector((state) => state.posts);
    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(userProfile(id));
    }, [id]);

    return (
        <>
            {userProfile ?
                <div style={{ maxWidth: "550px", margin: "0px auto" }}>
                    <div style={{
                        display: "flex",
                        justifyContent: "space-around",
                        margin: "18px 0px",
                        borderBottom: "1px solid grey"
                    }}>
                        <div>
                            <Avatar className={classes.purple} alt={user.name} src={user?.imageUrl}>{user?.name.charAt(0)}</Avatar>
                        </div>
                        <div>
                            <h4>{user.name}</h4>
                            <h5>{user.email}</h5>
                            <div style={{ display: "flex", justifyContent: "space-between", width: "108%" }}>
                                <h6>{post.length} posts</h6>
                                <h6> followers</h6>
                                <h6> following</h6>
                            </div>
                            {/* {showfollow ?
                                <button style={{
                                    margin: "10px"
                                }} className="btn waves-effect waves-light #64b5f6 blue darken-1"
                                    onClick={() => followUser()}
                                >
                                    Follow
                                </button>
                                :
                                <button
                                    style={{
                                        margin: "10px"
                                    }}
                                    className="btn waves-effect waves-light #64b5f6 blue darken-1"
                                    onClick={() => unfollowUser()}
                                >
                                    UnFollow
                                </button>
                            } */}
                        </div>
                    </div>
                    <div className="gallery">
                        {
                            post.map(item => {
                                return (
                                    <img key={item._id} className="item" src={item.file} alt={item.title} />
                                )
                            })
                        }
                    </div>
                </div>
                : <h2>loading...!</h2>
            }
        </>
    );
}

export default Profile;
