import React, { useState } from 'react';

//ui packages
import { Container, Avatar, Paper, Grid, Typography, Button, TextField } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import GoogleLogin from 'react-google-login';

//style
import useStyles from './Styles';
import InputField from './InputField';

const Auth = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [isSignUp, setIsSignUp] = useState(false);
    const classes = useStyles();

    const handlePasswordShow = () => setShowPassword((prvShowPassword) => !prvShowPassword);

    const handleSubmit = () => {

    };

    const handleOnChange = () => {

    };

    const switchMode = () => {
        setIsSignUp((prvIssignUp) => !prvIssignUp);
        setShowPassword(false);
    };

    return (
        <Container component="main" maxWidth="xs">
            <Paper className={classes.paper} elevation={3}>
                <Avatar className={classes.avatar}>
                    <LockOutlinedIcon />
                </Avatar>
                <Typography variant='h5'>{isSignUp ? "Sign Up" : "Sign In"}</Typography>
                <form className={classes.form} onSubmit={handleSubmit}>
                    <Grid container spacing={2}>
                        {
                            isSignUp && (
                                <>
                                    <InputField
                                        name="firstName"
                                        label='firstName'
                                        handleOnChange={handleOnChange}
                                        autoFocus
                                        half
                                    />
                                    <InputField
                                        name="firstName"
                                        label='firstName'
                                        handleOnChange={handleOnChange}
                                        autoFocus
                                        half
                                    />
                                </>
                            )}
                        <InputField
                            name='email'
                            label='Email Address'
                            handleOnChange={handleOnChange}
                            type="email"
                        />
                        <InputField
                            name='password'
                            label='Password'
                            handleOnChange={handleOnChange}
                            type={showPassword ? 'text' : 'password'}
                            handlePasswordShow={handlePasswordShow}
                        />
                        {
                            isSignUp && <InputField name='confirmPassword' label="Repeat Password" handleOnChange={handleOnChange} type='password' />
                        }
                    </Grid>
                    <GoogleLogin
                        clientId=""
                         render={(renderPrpos)=>(
                            <Button className={classes.googleButton}  color='primary' fullWidth onClick={renderPrpos.onClick} disabled={renderPrpos.disabled} startIcon={<icon/>}/>
                         )}
                    />
                    <Button fullWidth type='submit' variant='contained' color='primary' className={classes.submit}>{isSignUp ? 'Sign Up' : 'sign In'}</Button>
                    <Grid container justify='flex-end'>
                        <Grid item>
                            <Button onClick={switchMode}>
                                {isSignUp ? "Already have an account? Sign In" : "Don't have account? Sign Up"}
                            </Button>
                        </Grid>

                    </Grid>
                </form>
            </Paper>
        </Container>
    );
}

export default Auth;
