import React, { useEffect } from 'react';

//packages
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

// packages ui
import { CircularProgress, Divider, Typography, Avatar, Button } from '@material-ui/core';

//component
import { follow, userProfile } from '../../actions/Posts';

const Profile = () => {
    const { id } = useParams();
    const { user, userPosts, isLoading } = useSelector((state) => state.posts);
    // const [showfollow, setShowFollow] = useSelector((state) => state ? !state.following.includes(id) : true)
    // console.log(showfollow);
    const users = JSON.parse(localStorage.getItem('profile'));
    const dispatch = useDispatch()
    const url = "http://localhost:5000/";
    const navigate = useNavigate();
    const userId = users?.result?.googleId || users?.result?._id;

    //dispatch user profile req
    useEffect(() => {
        dispatch(userProfile(id));
    }, [id]);

    //post deatil page 
    const openPost = (id) => navigate(`/posts/${id}`);

    const handleFollowUser = () => {
        const followId = userId;
        dispatch(follow(followId));
    };

    return (
        isLoading ? <CircularProgress /> : (
            <div style={{ maxWidth: "550px", margin: "0px auto" }}>
                <div style={{
                    display: "flex",
                    justifyContent: "space-around",
                    margin: "18px 0px",
                    borderBottom: "1px solid grey"
                }}>
                    <div>
                        <Avatar
                            style={{ width: "160px", height: "160px", borderRadius: "80px" }}
                            alt={user?.name}
                            src={user?.imageUrl}
                        >
                            {user?.name.charAt(0)}
                        </Avatar>
                    </div>
                    <div>
                        <Typography variant="h6"> {user?.name}</Typography>
                        <Typography variant="h6">{user?.email}</Typography>
                        <div style={{ display: "flex", justifyContent: "space-between", width: "108%" }}>
                            <h6>{userPosts?.length} posts</h6>
                            <h6> followers</h6>
                            <h6> following</h6>
                        </div>
                        <Button style={{
                            margin: "10px"
                        }} className="btn waves-effect waves-light #64b5f6 blue darken-1"
                            onClick={handleFollowUser}
                        >
                            Follow
                        </Button>
                        <Button
                            style={{
                                margin: "10px"
                            }}
                            className="btn waves-effect waves-light #64b5f6 blue darken-1"
                        // onClick={() => unfollowUser()}
                        >
                            UnFollow
                        </Button>
                    </div>
                </div>
                <div className="gallery">
                    <Typography gutterBottom variant='h5'> Your Posts : </Typography>
                    <Divider />
                    {/* user posts */}
                    {userPosts?.map(item => {
                        return (
                            <img
                                key={item._id}
                                style={{ cursor: "pointer" }}
                                className="item"
                                src={url + item.file}
                                alt={item.title}
                                onClick={() => openPost(item._id)}
                            />
                        )
                    })
                    }
                </div>
            </div>
        )
    );
}

export default Profile;
