import React, { useState, useEffect, useRef } from 'react';

//material ui package
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import useStyles from './Styles';

//packages
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
// import FileUpload from 'react-material-file-upload';

//components
import { createPost, updatePost } from '../../actions/Posts';

const Form = ({
  currentId, setCurrentId
}) => {
  const [postData, setPostData] = useState({
    title: '',
    message: ' ',
    tags: '',
    file: null
  })
  const post = useSelector((state) => (currentId ? state.posts.posts.find((message) => message._id === currentId) : null));
  const user = JSON.parse(localStorage.getItem('profile'));
  const dispatch = useDispatch();
  const classes = useStyles();
  const navigate = useNavigate();
  const inputRef = useRef();

  // onmonut
  useEffect(() => {
    if (post) setPostData(post);
  }, [post]);

  /**
   * Reset input box
   */
  const clear = () => {
    setCurrentId(0);
    setPostData({ title: '', message: '', tags: '', file: null });
    inputRef.current.value = "";
  };

  /**
   * create user post 
   * @param {object} e 
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    const name = user?.result?.name;
    const formData = new FormData();
    for (const key of Object.keys(postData.file)) {
      formData.append('file', postData.file[key])
    }
    formData.append('title', postData.title);
    formData.append('tags', postData.tags);
    formData.append('message', postData.message);
    formData.append('name', name);
    if (currentId === 0) {
      dispatch(createPost(formData, navigate));
      clear();
    } else {
      dispatch(updatePost(currentId, formData, { name: user?.result?.name }));
      clear();
    }
  };

  if (!user?.result?.name) {
    return (
      <Paper className={classes.paper} elevation={6}>
        <Typography variant='h6' align='center'>
          Please Sign In to create your own memories and like other's memories...
        </Typography>
      </Paper>
    )
  }
  return (
    <Paper className={classes.paper} elevation={6}>
      <form
        autoComplete="off"
        noValidate
        className={`${classes.root} ${classes.form}`}
        onSubmit={handleSubmit}
        method='post'
        action='upload'
        enctype="multipart/form-data"
      >
        <Typography variant="h6">{currentId ? `Editing "${post.title}"` : 'Creating a Memory'}</Typography>
        <TextField
          name="title"
          variant="outlined"
          label="Title"
          fullWidth
          value={postData.title}
          onChange={(e) => setPostData({ ...postData, title: e.target.value })}
        />
        <TextField
          spellCheck={true}
          name="message"
          variant="outlined"
          label="Message"
          fullWidth
          multiline
          minRows={4}
          value={postData.message}
          onChange={(e) => setPostData({ ...postData, message: e.target.value })}
        />
        <TextField
          name="tags"
          variant="outlined"
          label="Tags (coma separated)"
          fullWidth
          value={postData.tags}
          onChange={(e) => setPostData({ ...postData, tags: e.target.value.split(',') })}
        />
        <div className={classes.fileInput}>
          <input
            name="file"
            type="file"
            ref={inputRef}
            onChange={(e) => setPostData({ ...postData, file: e.target.files })}
          />
        </div>
        <Button
          className={classes.buttonSubmit}
          variant="contained"
          color="primary"
          size="large"
          type="submit"
          fullWidth
        >Submit
        </Button>
        <Button
          variant="contained"
          color="secondary"
          size="large"
          onClick={clear}
          fullWidth
        >Reset
        </Button>
      </form>
    </Paper>
  );
};

export default Form;
