// action type component
import { AUTH, RESET_PASSWORD } from '../constants/actionTypes';

// packages
import { toast } from 'react-toastify';

// api 
import * as api from '../api/Index.js';

/**
 *  user signin 
 * @param {object} formData 
 * @param {} navigate  
 */
export const signin = (formData, navigate) => async (dispatch) => {
    try {
        const { data } = await api.signIn(formData);
        console.log(data)
        dispatch({ type: AUTH, data })
        navigate('/');
        toast.success(data.message, {
            position: "bottom-right",
            type: "success",
        });
    } catch (error) {
        console.log(error);
    }
};

/**
 * user signup 
 * @param {object} formData 
 * @param {*} navigate 
 */
export const signup = (formData, navigate) => async (dispatch) => {
    try {
        const { data } = await api.signUp(formData);
        dispatch({ type: AUTH, data });
        navigate('/');
        toast.success("Sign Up successfuly", {
            position: "bottom-right",
        })
    } catch (error) {
        console.log(error);
    }
};

export const resetPassword = (email) => async (dispatch) => {
    try {
        const { data } = await api.resetPasswordApi(email);
        dispatch({ type: RESET_PASSWORD, payload: data })
    } catch (error) {
        console.log(error);
    }
};


export const newPasswordGen = (password, token) => async (dispatch) => {
    try {
        const { data } = await api.newPasswordApi(password, token);
        dispatch({ type: "NEW_PASSWORD", data });
    } catch (error) {
        console.log(error);
    }
};
