import React, { useState, useEffect, useRef } from 'react';

//material ui package
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import useStyles from './Styles';

//packages
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';
import { useNavigate } from 'react-router-dom';

//components
import { createPost, updatePost } from '../../actions/Posts';

const Form = ({ currentId, setCurrentId }) => {
  // const [postData, setPostData] = useState({
  //   title: '',
  //   message: '',
  //   tags: '',
  //   selectedFile: null
  // });
  const [title, setTitle] = useState('');
  const [message, setMessage] = useState('');
  const [tags, setTags] = useState('');
  const [file, setFile] = useState(null);
  const post = useSelector((state) => (currentId ? state.posts.posts.find((message) => message._id === currentId) : null));
  const dispatch = useDispatch();
  const classes = useStyles();
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('profile'));
  const inputRef = useRef(null);

  // onmonut
  useEffect(() => {
    if (post) setPostData(post);
  }, [post]);

  /**
   * Reset input box
   */
  const clear = () => {
    setCurrentId(0);
    setFile(null);
    setMessage('');
    setTags('');
    setTitle('');
  };

  /**
   * create user post 
   * @param {object} e 
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    let formData = new FormData();
    formData.set('file', file);
    formData.set('title', title);
    formData.set('message', message);
    formData.set('tags', tags);


    if (currentId === 0) {
      dispatch(createPost({ formData, name: user?.result?.name }, navigate));
      clear();
    } else {
      dispatch(updatePost(currentId, { formData, name: user?.result?.name }));
      clear();
    }
  };

  if (!user?.result?.name) {
    return (
      <Paper className={classes.paper} elevation={6}>
        <Typography variant='h6' align='center'>
          Please Sign In to create your own memories and like other's memories...
        </Typography>
      </Paper>
    )
  }
  return (
    <Paper className={classes.paper} elevation={6}>
      <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit} enctype="multipart/form-data" action="/upload">
        <Typography variant="h6">{currentId ? `Editing "${post.title}"` : 'Creating a Memory'}</Typography>

        <TextField
          name="title"
          variant="outlined"
          label="Title"
          fullWidth
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        // onChange={(e) => setPostData({ ...postData, title: e.target.value })}
        />
        <TextField
          spellCheck={true}
          name="message"
          variant="outlined"
          label="Message"
          fullWidth
          multiline
          rows={4}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <TextField
          name="tags"
          variant="outlined"
          label="Tags (coma separated)"
          fullWidth
          value={tags}
          onChange={(e) => setTags(e.target.value.split(','))}
        />
        <div className={classes.fileInput}>
          {/* <FileBase
            type="file"
            onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} 
          /> */}
          <input
            name="file"
            type="file"
            // value={postData.selectedFile}
            onChange={(e) => setFile(e.target.files[0])}
          />
        </div>
        <Button
          className={classes.buttonSubmit}
          variant="contained"
          color="primary"
          size="large"
          type="submit"
          fullWidth
        >Submit
        </Button>
        <Button
          variant="contained"
          color="secondary"
          size="large"
          onClick={clear}
          fullWidth
        >Reset
        </Button>
      </form>
    </Paper>
  );
};

export default Form;
