import React, { useState } from 'react';

//ui packages
import { Container, Avatar, Paper, Grid, Typography, Button } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import GoogleLogin from 'react-google-login';

//style
import useStyles from './Styles';

// components
import InputField from './InputField';
import Icon from './icon';

const Auth = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [isSignUp, setIsSignUp] = useState(false);
    const classes = useStyles();

    const handlePasswordShow = () => setShowPassword((prvShowPassword) => !prvShowPassword);

    const handleSubmit = () => {

    };

    const handleOnChange = () => {

    };

    const switchMode = () => {
        setIsSignUp((prvIssignUp) => !prvIssignUp);
        setShowPassword(false);
    };

    const googleSuccess = (res) => {
        console.log(res, "Google Sign In was successful.");
    };

    const googleFaliure = () => {
        console.log("Google Sign In was unsuccessful. Try Again Later");
    };

    return (
        <Container component="main" maxWidth="xs">
            <Paper className={classes.paper} elevation={3}>
                <Avatar className={classes.avatar}>
                    <LockOutlinedIcon />
                </Avatar>
                <Typography variant='h5'>{isSignUp ? "Sign Up" : "Sign In"}</Typography>
                <form className={classes.form} onSubmit={handleSubmit}>
                    <Grid container spacing={2}>
                        {
                            isSignUp && (
                                <>
                                    <InputField
                                        name="firstName"
                                        label='firstName'
                                        handleOnChange={handleOnChange}
                                        autoFocus
                                        half
                                    />
                                    <InputField
                                        name="firstName"
                                        label='firstName'
                                        handleOnChange={handleOnChange}
                                        autoFocus
                                        half
                                    />
                                </>
                            )}
                        <InputField
                            name='email'
                            label='Email Address'
                            handleOnChange={handleOnChange}
                            type="email"
                        />
                        <InputField
                            name='password'
                            label='Password'
                            handleOnChange={handleOnChange}
                            type={showPassword ? 'text' : 'password'}
                            handlePasswordShow={handlePasswordShow}
                        />
                        {
                            isSignUp && <InputField name='confirmPassword' label="Repeat Password" handleOnChange={handleOnChange} type='password' />
                        }
                    </Grid>
                    <GoogleLogin
                        clientId="968502154569-nr7f8f5i2noagg07ml7jde4a5jivvl78.apps.googleusercontent.com"
                        render={(renderPrpos) => (
                            <Button
                                className={classes.googleButton}
                                color='primary'
                                fullWidth
                                onClick={renderPrpos.onClick}
                                disabled={renderPrpos.disabled}
                                startIcon={<Icon />}
                                variant='contained'
                            >
                                Google Sign In
                            </Button>
                        )}
                        onSuccess={googleSuccess}
                        onFailure={googleFaliure}
                        cookiePolicy="single_host_orgin"
                    />
                    <Button fullWidth type='submit' variant='contained' color='primary' className={classes.submit}>{isSignUp ? 'Sign Up' : 'sign In'}</Button>
                    <Grid container justify='flex-end'>
                        <Grid item>
                            <Button onClick={switchMode}>
                                {isSignUp ? "Already have an account? Sign In" : "Don't have account? Sign Up"}
                            </Button>
                        </Grid>

                    </Grid>
                </form>
            </Paper>
        </Container>
    );
}

export default Auth;
