import React, { useEffect, useState } from 'react';

//packages
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import moment from 'moment';

// packages ui
import {
    CircularProgress,
    Divider,
    Typography,
    Avatar,
    Button,
    Card,
    CardHeader,
    CardContent,
    CardMedia,
    IconButton,
    CardActions,
} from '@material-ui/core';
import CommentIcon from '@material-ui/icons/Comment';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import MoreVertIcon from '@material-ui/icons/MoreVert';

//component
import { follow, unFollow, userProfile } from '../../actions/Posts';

const Profile = () => {
    const { id } = useParams();
    const { user, userPosts, isLoading } = useSelector((state) => state.posts);
    const [following, setFollowing] = useState();
    const users = JSON.parse(localStorage.getItem('profile'));
    const dispatch = useDispatch()
    const url = "http://localhost:5000/";
    const navigate = useNavigate();
    const followId = users?.result?.googleId || users?.result?._id;
    const [showfollow, setShowFollow] = useState(!users?.followers?.indexOf(user._id))
  console.log(following)

    //dispatch user profile req
    useEffect(() => {
        dispatch(userProfile(id));
    }, [id]);

    //post deatil page 
    const openPost = (id) => navigate(`/posts/${id}`);

    /**
     * follow user by user id
     * @param {object} user 
     */
    const handleFollowUser = (user) => {
        dispatch(follow(followId, user, setFollowing));
        setShowFollow(false)
    };

    /**
     * unfollow user 
     * @param {object} user 
     */
    const hnadleUnFollowUSer = (user) => {
        const unFollowId = followId;
        dispatch(unFollow(unFollowId, user, setFollowing));
        setShowFollow(true)
    };




    return (
        isLoading ? <CircularProgress /> : (
            <div style={{ maxWidth: "550px", margin: "0px auto" }}>
                <div style={{
                    display: "flex",
                    justifyContent: "space-around",
                    margin: "18px 0px",
                    borderBottom: "1px solid grey"
                }}>
                    <div>
                        <Avatar
                            style={{ width: "160px", height: "160px", borderRadius: "80px" }}
                            alt={user?.name}
                            src={user?.imageUrl}
                        >
                            {user?.name.charAt(0)}
                        </Avatar>
                    </div>
                    <div>
                        <Typography variant="h6"> {user?.name}</Typography>
                        <Typography variant="h6">{user?.email}</Typography>
                        <div style={{ display: "flex", justifyContent: "space-between", width: "108%" }}>
                            <h6>{userPosts?.length} posts</h6>
                            <h6>{user?.followers?.length} followers</h6>
                            <h6>{following?.length === 1 ?
                                (<>{following?.length}</>
                                ) : (
                                    <>{user?.following?.length}</>)
                            } following
                            </h6>
                        </div>
                        {users?.result?._id === user?._id ? (null) : (
                            <>
                                {!showfollow ? (
                                    <Button
                                        style={{
                                            margin: "10px"
                                        }}
                                        variant='outlined'
                                        color='primary'
                                        onClick={() => handleFollowUser(user)}
                                    >Follow
                                    </Button>
                                ) : (
                                    <Button
                                        style={{
                                            margin: "10px"
                                        }}
                                        variant='contained'
                                        color='primary'
                                        onClick={() => hnadleUnFollowUSer(user)}
                                    >UnFollow
                                    </Button>
                                )}
                            </>)
                        }
                    </div>
                </div>
                <div className="gallery">
                    <Typography gutterBottom variant='h5'> Your Posts : </Typography>
                    <Divider />
                    {/* user posts */}
                    {userPosts?.map(item => {
                        return (
                            <Card sx={{ maxWidth: 845 }} key={item._id}>
                                <CardHeader
                                    avatar={
                                        <Avatar
                                            alt={user?.name}
                                            src={user?.imageUrl}
                                        >
                                            {user?.name.charAt(0)}
                                        </Avatar>
                                    }
                                    action={
                                        <IconButton aria-label="settings">
                                            <MoreVertIcon />
                                        </IconButton>
                                    }
                                    title={user?.name}
                                    subheader={moment(item.createdAt).fromNow()}
                                />
                                <CardMedia
                                    component="img"
                                    height="194"
                                    image={url + item?.file}
                                    alt={item?.title}
                                    onClick={() => openPost(item._id)}
                                    style={{ cursor: "pointer" }}
                                />
                                <CardContent>
                                    <Typography variant="body2" color="text.secondary">
                                        {item?.title}
                                    </Typography>
                                </CardContent>
                                <CardActions disableSpacing>
                                    <IconButton aria-label="add to favorites">
                                        <ThumbUpAltIcon />
                                        &nbsp;
                                        <p style={{ fontSize: "16px", }}>
                                            {item?.likes.length} {item.likes.length === 1 ? 'Like' : 'Likes'}
                                        </p>
                                    </IconButton>
                                    <IconButton aria-label="share">
                                        <CommentIcon />
                                        &nbsp;
                                        <p style={{ fontSize: "16px", }}>
                                            {item?.comments.length} {item?.comments.length === 0 ? 'comment' : 'comments'}
                                        </p>
                                    </IconButton>
                                </CardActions>
                            </Card>
                        )
                    })}
                </div>
            </div >
        )
    );
}

export default Profile;
