import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { userProfile } from '../../actions/Posts';
import { Avatar } from '@material-ui/core';
import { CircularProgress, Divider, Typography } from '@material-ui/core';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
    const { id } = useParams();
    const { user, userPosts, isLoading } = useSelector((state) => state.posts);
    const dispatch = useDispatch()
    const url = "http://localhost:5000/";
    const navigate = useNavigate();


    //dispatch user profile req
    useEffect(() => {
        dispatch(userProfile(id));
    }, [id]);
    const openPost = (id) => navigate(`/posts/${id}`);
    return (
        isLoading ? <CircularProgress /> : (
            <div style={{ maxWidth: "550px", margin: "0px auto" }}>
                <div style={{
                    display: "flex",
                    justifyContent: "space-around",
                    margin: "18px 0px",
                    borderBottom: "1px solid grey"
                }}>
                    <div>
                        <Avatar
                            style={{ width: "160px", height: "160px", borderRadius: "80px" }}
                            alt={user?.name}
                            src={user?.imageUrl}
                        >
                            {user?.name.charAt(0)}
                        </Avatar>
                    </div>
                    <div>
                        <h4>{user?.name}</h4>
                        <h5>{user?.email}</h5>
                        <div style={{ display: "flex", justifyContent: "space-between", width: "108%" }}>
                            <h6>{userPosts?.length} posts</h6>
                            <h6> followers</h6>
                            <h6> following</h6>
                        </div>

                        <button style={{
                            margin: "10px"
                        }} className="btn waves-effect waves-light #64b5f6 blue darken-1"
                        // onClick={() => followUser()}
                        >
                            Follow
                        </button>

                        <button
                            style={{
                                margin: "10px"
                            }}
                            className="btn waves-effect waves-light #64b5f6 blue darken-1"
                        // onClick={() => unfollowUser()}
                        >
                            UnFollow
                        </button>

                    </div>
                </div>
                <div className="gallery">
                    <Typography gutterBottom variant='h5'> You might also like : </Typography>
                    <Divider />
                    {userPosts.map(item => {
                        return (
                            <img
                                key={item._id}
                                style={{ cursor: "pointer" }}
                                className="item"
                                src={url + item.file}
                                alt={item.title}
                                onClick={() => openPost(item._id)}
                            />
                        )
                    })
                    }
                </div>
            </div>
        )
    );
}

export default Profile;
