import React, { useEffect, useState } from 'react';

//packages
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import moment from 'moment';

// packages ui
import {
    CircularProgress,
    Divider,
    Typography,
    Avatar,
    Button,
    Card,
    CardHeader,
    CardContent,
    CardMedia,
    IconButton,
    ButtonBase,
    CardActions
} from '@material-ui/core';
import Skeleton from '@material-ui/lab/Skeleton';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';


//component
import { follow, unFollow, userProfile } from '../../actions/Posts';

const Profile = () => {
    const { id } = useParams();
    const { user, userPosts, isLoading } = useSelector((state) => state.posts);
    const users = JSON.parse(localStorage.getItem('profile'));
    const dispatch = useDispatch()
    const url = "http://localhost:5000/";
    const navigate = useNavigate();
    const followId = users?.result?.googleId || users?.result?._id;
    const [showfollow, setShowFollow] = useState(user ? !user?.following?.includes(followId) : true)
    console.log(followId)
    //dispatch user profile req
    useEffect(() => {
        dispatch(userProfile(id));
    }, [id]);

    //post deatil page 
    const openPost = (id) => navigate(`/posts/${id}`);

    /**
     * follow user by user id
     * @param {object} user 
     */
    const handleFollowUser = (user) => {
        dispatch(follow(followId, user));
        setShowFollow(false)
    };

    /**
     * unfollow user 
     * @param {object} user 
     */
    const hnadleUnFollowUSer = (user) => {
        const unFollowId = followId;
        dispatch(unFollow(unFollowId, user));
        setShowFollow(true)
    };

    return (
        isLoading ? <CircularProgress /> : (
            <div style={{ maxWidth: "550px", margin: "0px auto" }}>
                <div style={{
                    display: "flex",
                    justifyContent: "space-around",
                    margin: "18px 0px",
                    borderBottom: "1px solid grey"
                }}>
                    <div>
                        <Avatar
                            style={{ width: "160px", height: "160px", borderRadius: "80px" }}
                            alt={user?.name}
                            src={user?.imageUrl}
                        >
                            {user?.name.charAt(0)}
                        </Avatar>
                    </div>
                    <div>
                        <Typography variant="h6"> {user?.name}</Typography>
                        <Typography variant="h6">{user?.email}</Typography>
                        <div style={{ display: "flex", justifyContent: "space-between", width: "108%" }}>
                            <h6>{userPosts?.length} posts</h6>
                            <h6>{user?.followers?.length} followers</h6>
                            <h6>{user?.following?.length} following</h6>
                        </div>
                        {users?.result?._id === user?._id ? (null) : (
                            <>
                                {showfollow ? (
                                    <Button
                                        style={{
                                            margin: "10px"
                                        }}
                                        variant='outlined'
                                        color='primary'
                                        onClick={() => handleFollowUser(user)}
                                    >Follow
                                    </Button>
                                ) : (
                                    <Button
                                        style={{
                                            margin: "10px"
                                        }}
                                        variant='contained'
                                        color='primary'
                                        onClick={() => hnadleUnFollowUSer(user)}
                                    >UnFollow
                                    </Button>
                                )}
                            </>)
                        }
                    </div>
                </div>
                <div className="gallery">
                    <Typography gutterBottom variant='h5'> Your Posts : </Typography>
                    <Divider />
                    {/* user posts */}
                    {userPosts?.map(item => {
                        return (
                            <Card sx={{ maxWidth: 345 }}>
                                <CardHeader
                                    avatar={
                                        <Avatar
                                            alt={user?.name}
                                            src={user?.imageUrl}
                                        >
                                            {user?.name.charAt(0)}
                                        </Avatar>
                                    }
                                    action={
                                        <IconButton aria-label="settings">
                                            <MoreVertIcon />
                                        </IconButton>
                                    }
                                    title={user?.name}
                                    subheader={moment(item.createdAt).fromNow()}
                                />
                                <CardMedia
                                    component="img"
                                    height="194"
                                    image={url + item?.file}
                                    alt={item?.title}
                                />
                                <CardContent>
                                    <Typography variant="body2" color="text.secondary">
                                        {item?.title}
                                    </Typography>
                                </CardContent>
                                <CardActions disableSpacing>
                                    <IconButton aria-label="add to favorites">
                                        <FavoriteIcon />
                                    </IconButton>
                                    <IconButton aria-label="share">
                                        <ShareIcon />
                                    </IconButton>
                                    <ExpandMore
                                        expand={expanded}
                                        onClick={handleExpandClick}
                                        aria-expanded={expanded}
                                        aria-label="show more"
                                    >
                                        <ExpandMoreIcon />
                                    </ExpandMore>
                                </CardActions>
                                <Collapse in={expanded} timeout="auto" unmountOnExit>
                                    <CardContent>
                                        <Typography paragraph>Method:</Typography>
                                        <Typography paragraph>
                                            Heat 1/2 cup of the broth in a pot until simmering, add saffron and set
                                            aside for 10 minutes.
                                        </Typography>
                                        <Typography paragraph>
                                            Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over
                                            medium-high heat. Add chicken, shrimp and chorizo, and cook, stirring
                                            occasionally until lightly browned, 6 to 8 minutes. Transfer shrimp to a
                                            large plate and set aside, leaving chicken and chorizo in the pan. Add
                                            piment√≥n, bay leaves, garlic, tomatoes, onion, salt and pepper, and cook,
                                            stirring often until thickened and fragrant, about 10 minutes. Add
                                            saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
                                        </Typography>
                                        <Typography paragraph>
                                            Add rice and stir very gently to distribute. Top with artichokes and
                                            peppers, and cook without stirring, until most of the liquid is absorbed,
                                            15 to 18 minutes. Reduce heat to medium-low, add reserved shrimp and
                                            mussels, tucking them down into the rice, and cook again without
                                            stirring, until mussels have opened and rice is just tender, 5 to 7
                                            minutes more. (Discard any mussels that don&apos;t open.)
                                        </Typography>
                                        <Typography>
                                            Set aside off of the heat to let rest for 10 minutes, and then serve.
                                        </Typography>
                                    </CardContent>
                                </Collapse>
                            </Card>
                        )
                    })}
                </div>
            </div >
        )
    );
}

export default Profile;
